from agno.agent import Agent
from agno.models.openai import OpenAIChat

from app.models.input import EvergreenInput
from app.models.output import EvergreenOutput
from app.prompts import EVERGREEN_EXTRACTOR

from app.core.config import get_settings

settings = get_settings()

extractor_agent = Agent(
    name="Extractor of Evergreening Details",
    model=OpenAIChat(
        id="gpt-4.1",
        api_key=settings.openai_api_key
    ),
    instructions=EVERGREEN_EXTRACTOR,
    input_schema=EvergreenInput,
    output_schema=EvergreenOutput,
    stream=False,
    markdown=True,
    debug_mode=settings.debug_mode,
    reasoning=True
)

